import React, { Component } from 'react'
import ApiService from "../../service/ApiService";

class ListRecord extends Component {

    constructor(props) {
        super(props)
        this.state = {
            record: []
        }
        this.reloadUserList = this.reloadUserList.bind(this);
        // this.onSearch = this.onSearch.bind(this);
    }

    componentDidMount() {
        this.reloadUserList();
    }

    reloadUserList() {
        ApiService.fetchRecords()
            .then((res) => {
                this.setState({ record: res.data })
            }).catch(error => {
                console.log(error)
            });
    }

    onSearch = (e) => {
        this.setState((prevState) => {
            if (e.target.value !== null || e.target.value !== '') {

                return { record: prevState.record.filter(f => f.plate.includes(e.target.value) || f.owner.includes(e.target.value)) }
            } else {
                return { record: prevState.record };
            }
        })
    }

    render() {
        return (
            <div>
                <h2 className="text-center">Record Details</h2>
                <div className="form-group">
                    <label>Search</label>
                    <input type="text" placeholder="Search plate or owner" name="search" className="form-control" onChange={this.onSearch} />
                </div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Plate Number</th>
                            <th>Owner</th>
                            <th>Start Date</th>
                            <th>End Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.record.length > 0 ? this.state.record.map(
                                record =>
                                    <tr key={record.plate}>
                                        <td>{record.plate}</td>
                                        <td>{record.owner}</td>
                                        <td>{record.start_date}</td>
                                        <td>{record.end_date}</td>
                                    </tr>
                            ) : null
                        }
                    </tbody>
                </table>

            </div>
        );
    }

}

export default ListRecord;
