import axios from 'axios';

const RECORD_API_BASE_URL = 'http://localhost:5000/plate';

class ApiService {

    fetchRecords() {
        return axios.get(RECORD_API_BASE_URL);
    }

    addRecord(plate) {
        return axios.post(""+RECORD_API_BASE_URL, plate);
    }

}

export default new ApiService();
