import React, { Component } from "react";
import ApiService from "../../service/ApiService";

class ListRecord extends Component {
  constructor(props) {
    super(props);
    this.state = {
      record: [],
      filteredRecord: [],
    };
    this.reloadUserList = this.reloadUserList.bind(this);
  }

  componentDidMount() {
    this.reloadUserList();
  }

  reloadUserList() {
    ApiService.fetchRecords()
      .then((res) => {
        this.setState({ record: res.data, filteredRecord: res.data });
      })
      .catch((error) => {
        console.log(error);
      });
  }

  onSearch = (e) => {
    const { record } = this.state;
    if (e.target.value !== null || e.target.value !== "") {
      this.setState({
        filteredRecord: record.filter(
          (f) =>
            f.plate.toUpperCase().includes(e.target.value.toUpperCase()) ||
            f.owner.toUpperCase().includes(e.target.value.toUpperCase())
        ),
      });
    } else {
      this.setState({ filteredRecord: record });
    }
  };

  onSort = () => {
    console.log("sort");
    debugger;
    const sortedRecords = this.state.filteredRecord.sort((a, b) => {
      return a - b;
    });

    this.setState({ filteredRecord: sortedRecords });
  };

  render() {
    return (
      <div>
        <h2 className="text-center">Record Details</h2>
        <div className="form-group">
          <label>Search</label>
          <input
            type="text"
            placeholder="Search plate or owner"
            name="search"
            className="form-control"
            onChange={this.onSearch}
          />
        </div>
        <table className="table table-striped">
          <thead>
            <tr>
              <th>
                Plate Number{" "}
                <span onClick={this.onSort}>
                  <i className="bi bi-sort-down"></i>
                </span>
                {/* <div className="row">
                  <div className="col">Plate Number</div>
                  <div className="col">
                    <span>
                      <i className="bi bi-sort-down"></i>
                    </span>
                  </div>
                </div> */}
              </th>
              <th>Owner</th>
              <th>Start Date</th>
              <th>End Date</th>
            </tr>
          </thead>
          <tbody>
            {this.state.filteredRecord.length > 0
              ? this.state.filteredRecord.map((record) => (
                  <tr key={record.plate}>
                    <td>{record.plate}</td>
                    <td>{record.owner}</td>
                    <td>{record.start_date}</td>
                    <td>{record.end_date}</td>
                  </tr>
                ))
              : null}
          </tbody>
        </table>
      </div>
    );
  }
}

export default ListRecord;
