import React, { Component } from 'react'
import ApiService from "../../service/ApiService";

class AddRecord extends Component {

    constructor(props) {
        super(props);
        this.state = {
            plate: '',
            owner: '',
            start_date: '',
            end_date: '',
        }
        this.addRecord = this.addRecord.bind(this);
    }

    addRecord = (e) => {
        e.preventDefault();
        let record = { plate: this.state.plate, owner: this.state.owner, start_date: this.state.start_date, end_date: this.state.end_date };
        ApiService.addRecord(record)
            .then(res => {
                this.setState({ message: 'Record added successfully.' });
                this.props.history.push('/records');
            });
    }

    onChange = (e) =>
        this.setState({ [e.target.name]: e.target.value });

    render() {
        return (
            <div>
                <h2 className="text-center">Add Record</h2>
                <form>
                    <div className="form-group">
                        <label>Plate</label>
                        <input type="text" placeholder="Plate Number" name="plate" className="form-control" value={this.state.plate} onChange={this.onChange} />
                    </div>

                    <div className="form-group">
                        <label>Owner</label>
                        <input type="text" placeholder="Owner" name="owner" className="form-control" value={this.state.owner} onChange={this.onChange} />
                    </div>

                    <div className="form-group">
                        <label>Start Date
                        </label>
                        <input placeholder="Start Date" name="start_date" type="date" className="form-control" value={this.state.start_date} onChange={this.onChange} />
                    </div>

                    <div className="form-group">
                        <label>End Date</label>
                        <input placeholder="End Date" name="end_date" className="form-control" value={this.state.end_date} onChange={this.onChange} />
                    </div>
                    <button className="btn btn-success" onClick={this.addRecord}>Save</button>
                </form>
            </div>
        );
    }
}

export default AddRecord;
